import './App.css';
import React, { Component } from "react";

class App extends Component {
  state={
    value: "",
    clue1: false,
    clue2: false,
    clue3: false
  }
  handleChange = (event) => {
      this.setState({value: event.target.value});
    }

  handleSubmit = (event) => {
    if (this.state.clue1 == false && this.state.value === "clue1") {
        this.setState({clue1: true})
        event.preventDefault();
    } else {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    }

    if (this.state.clue1 == true && this.state.clue2 == false && this.state.value === "clue2") {
        this.setState({clue1: true})
        event.preventDefault();
    } else {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    }
  }


  render() {
    return (
        <div>
            <header>
                <h1>Escape Berlin!</h1>
                {this.state.clue1 == false && <h2>Clue 1</h2>}
                {this.state.clue1 == true && this.state.clue2 == false && <h2>Clue 2</h2>}
            </header>
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            <input type="text" value={this.state.value} onChange={this.handleChange} placeholder = "Your Code..."/>
                        </label>
                        <input type="submit" value="Submit" />
                    </form>
                </div>
        </div>
    );
  }
}

export default App;
