import './App.css';
import React, { Component } from "react";



class Clue2 extends Component {
     handleLocation2 = (event) => {
        window.open("https://www.google.com/maps/place/Brandenburg+Gate/@52.5162746,13.3755154,17z/data=!4m12!1m6!3m5!1s0x47a851c655f20989:0x26bbfb4e84674c63!2sBrandenburg+Gate!8m2!3d52.5162746!4d13.3777041!3m4!1s0x47a851c655f20989:0x26bbfb4e84674c63!8m2!3d52.5162746!4d13.3777041");
        event.preventDefault();
     }

    render(){
        return(
            <div className="Clue2">
                <h2>Clue 2</h2>
                <img src="https://upload.wikimedia.org/wikipedia/commons/a/a4/Holocaust-Mahnmal_Berlin_2006.jpg" width="300" height="200" alt="Holocaust Meomorial"/>
                <br></br>
                <button onClick={this.handleLocation2}>Get Next Location</button>
                <p>A place to remember. A place to reflect.</p>
                <p>At the very center, I left an object</p>
            </div>
        );
    }
}

export default Clue2;
