import './App.css';
import React, { Component } from "react";



class Clue1 extends Component {
     handleLocation1 = (event) => {
        window.open("https://www.google.com/maps/place/Brandenburg+Gate/@52.5162746,13.3755154,17z/data=!3m1!4b1!4m5!3m4!1s0x47a851c655f20989:0x26bbfb4e84674c63!8m2!3d52.5162746!4d13.3777041");
        event.preventDefault();
     }

    render(){
        return(
        <div className="clue1-header">
            <h2>Clue 1</h2>
            <p>Are you at Checkpoint Charlie? Yes? “You are entering the American Sector”</p>
                <img src="https://commons.wikimedia.org/wiki/File:Checkpoint_Charlie_Berlin.jpg"
                    alt="Checkpoint Charlie"/>
            <div className="google-map-code">
                <iframe title="Map_to_Brandenburg" src="https://www.google.com/maps/embed?pb=!1m24!1m8!1m3!1d1213.941766078946!2d13.376191858280583!3d52.517446794953415!3m2!1i1024!2i768!4f13.1!4m13!3e2!4m5!1s0x47a851c748c80e45%3A0xbf985f98f930a812!2sReichstag%20Building%2C%20Platz%20der%20Republik%2C%20Berlin!3m2!1d52.518619!2d13.3763176!4m5!1s0x47a851c655f20989%3A0x26bbfb4e84674c63!2sBrandenburg%20Gate%2C%20Pariser%20Platz%2C%2010117%20Berlin!3m2!1d52.516274599999996!2d13.377704099999999!5e0!3m2!1sen!2sde!4v1657197996607!5m2!1sen!2sde" width="500" height="400" style={{border:0}} allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
                <br></br>
                <button className="Button" onClick={this.handleLocation1}>Open in Maps</button>
            </div>
        </div>
        );
    }
}

export default Clue1;
