import './App.css';
import React, { Component } from "react";



class Clue4 extends Component {
    constructor(props) {
        super(props);
        this.state = {note: false};
    }



     handleLocation2 = (event) => {
        window.open("https://www.google.com/maps/place/Information+Panel+-+%E2%80%9CF%C3%BChrerbunker%22/@52.5125237,13.3787868,17z/data=!3m1!4b1!4m5!3m4!1s0x47a8513c4f251d4f:0xe229c8801b2fa179!8m2!3d52.5125237!4d13.3809755");
        event.preventDefault();
     }
     handleNote = (event) => {
        this.setState({note: true})
        event.preventDefault();
     }
     handleClose = (event) => {
         this.setState({note: false})
         event.preventDefault();
     }

    render(){
        const Note = () => (
            <div className="Note">
                <h2 className="note_message">To the agent who finds this, </h2>
                <h2 className="note_message"> I have uncovered plans by an Anti-Nazi extremist group, the Baum Group, to assassinate Parliament members and blow up the Reichstag, the symbol of our German democracy. The members of the Baum Group are Holocaust survivors that believed that the Nuremberg Trials were not enough and wish to take revenge for their lost ones. Their current targets are a handful of prominent figures in the West Germany Parliamentary Council who once swore an oath to Hitler but have since repented. I’ve encrypted the bomb defusal code in clues using locations throughout the city. Use my decryption code on the pictures that you gathered along the way. I cannot be the one to stop this plot as I have been compromised by some members of the Baum Group. While it is too late for me, there is still time to prevent needless deaths. All hope is lost when the sun falls. </h2>
                <h2 className="note_message"> SUNSET </h2>
                <button className="Button" onClick={this.handleClose}>Close Note</button>
            </div>
        );
        return(
            <div className="clue1-header">
                <h2 className="message">Go to Hitler’s Bunker'</h2>
                <iframe title="Map_toBunker" src="https://www.google.com/maps/embed?pb=!1m26!1m12!1m3!1d5540.145853737504!2d13.386017692195674!3d52.51060503999877!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!4m11!3e2!4m3!3m2!1d52.5085416!2d13.381608!4m5!1s0x47a8513c4f251d4f%3A0xe229c8801b2fa179!2sInformation%20Panel%20-%20%E2%80%9CF%C3%BChrerbunker%22%2C%20Gertrud-Kolmar-Stra%C3%9Fe%2014%2C%2010117%20Berlin!3m2!1d52.512523699999996!2d13.3809755!5e0!3m2!1sen!2sde!4v1658239314462!5m2!1sen!2sde" width="600" height="450" style={{border:0}} allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
                <br></br>
                <button className="Button" onClick={this.handleLocation2}>Open in Maps</button>
                <h3 className="message">Along the wall to the right, you see on the ground a neatly folded black suit and a white button-down shirt stained red. You walk over to investigate. In the right breast pocket of the suit you find an envelope stamped with an S. You turn it over and open it to find a message scrawled in dark ink:</h3>
                {this.state.note===false && <button className="Button" onClick={this.handleNote}>Look at Note</button>}
                {this.state.note && <Note/>}
                <h2>Clue 4</h2>
                <h3 className="message">You made it to the site where Hitler’s bunker used to stand. Now just rubble buried beneath earth and concrete, nothing remains of the Führerbunker except the sign “Mythos und Geschightszeucnis Führerbunker.”</h3>
                <h3 className="message">Pay close attention to the title of that sign. Count words left to right, then letters right to left. (1-4) (3-6) (4-1) (4-7) (1-2) (4-9).</h3>

                <h3 className="message"> - Agent SUNSET </h3>
            </div>

        );
    }
}

export default Clue4;
