import './App.css';
import React, { Component } from "react";

class App extends Component {

  constructor() {
    super();

    this.state= {
        value: "",
        num_correct: 0
    }

  }

  setState(state) {
    window.localStorage.setItem('state', JSON.stringify(state));
    console.log(window.localStorage.getItem('state'));
    super.setState(state);
  }

  handleChange = (event) => {
      this.setState({value: event.target.value, num_correct: this.state.num_correct});
    }

  handleSubmit = (event) => {
    if (this.state.num_correct === 0 && this.state.value === "clue1") {
        this.setState({value: this.state.value, num_correct: 1})
        event.preventDefault();
    } else if (this.state.num_correct === 0 && this.state.value !== "clue1") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else if (this.state.num_correct === 1 && this.state.value === "clue2") {
        this.setState({value: this.state.value, num_correct: 2})
        event.preventDefault();
    } else if (this.state.num_correct === 1 && this.state.value !== "clue2") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else if (this.state.num_correct === 2 && this.state.value === "clue3") {
        this.setState({value: this.state.value, num_correct: 3})
        event.preventDefault();
    } else if (this.state.num_correct === 2 && this.state.value !== "clue3") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else if (this.state.num_correct === 3 && this.state.value === "clue4") {
        this.setState({value: this.state.value, num_correct: 4})
        event.preventDefault();
    } else if (this.state.num_correct === 3 && this.state.value !== "clue4") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else if (this.state.num_correct === 4 && this.state.value === "clue5") {
        this.setState({value: this.state.value, num_correct: 5})
        event.preventDefault();
    } else if (this.state.num_correct === 4 && this.state.value !== "clue5") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else if (this.state.num_correct === 5 && this.state.value === "clue6") {
        this.setState({value: this.state.value, num_correct: 6})
        event.preventDefault();
    } else if (this.state.num_correct === 5 && this.state.value !== "clue6") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else if (this.state.num_correct === 6 && this.state.value === "3 Big Balls") {
        this.setState({value: this.state.value, num_correct: 7})
        event.preventDefault();
    } else if (this.state.num_correct === 6 && this.state.value !== "3 Big Balls") {
        alert(this.state.value + " is not correct.");
        event.preventDefault();
    } else {
        event.preventDefault();
    }
    this.setState({value: ""});

  }


  render() {
    return (
        <div>
            <header>
                <h1>Escape Berlin!</h1>
                <p>Num_correct {this.state.num_correct}</p>
                {this.state.num_correct === 0 && <h2>Clue 1</h2>}
                {this.state.num_correct === 1 && <h2>Clue 2</h2>}
                {this.state.num_correct === 2 && <h2>Clue 3</h2>}
                {this.state.num_correct === 3 && <h2>Clue 4</h2>}
                {this.state.num_correct === 4 && <h2>Clue 5</h2>}
                {this.state.num_correct === 5 && <h2>Clue 6</h2>}
                {this.state.num_correct === 6 && <h2>Final Location</h2>}
            </header>
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            <input type="text" value={this.state.value} onChange={this.handleChange} placeholder = "Your Code..."/>
                        </label>
                        <input type="submit" value="Submit" />
                    </form>
                </div>
        </div>
    );
  }
}

export default App;
